import { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import CardList from './components/card-list/card-list';
import './components/card-list/card-list.component.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [],
      searchString:""
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(users => this.setState({
        monsters: users
      }));
  }
  onSearchChange = (event) =>{
    const searchString = event.target.value.toLocaleLowerCase();    
    this.setState(()=>{
      return {searchString};
    });
  }

  render() {
    const {monsters,searchString}=this.state;

    const fileteredMonsters = this.state.monsters.filter((monster)=>{
      return monster.name.toLocaleLowerCase().includes(this.state.searchString);
   });
    return (
      <>
        <input 
        className="search" 
        type="search" 
        placeholder='search monsters' 
        onChange={
          //console.log({startingArray:this.state.monsters});
          this.onSearchChange

          }/>
        <CardList>
          {
            fileteredMonsters.map(monster => <h5 key={monster.id}>{monster.name}</h5>)
          }
        </CardList>

      </>
    );
  }
}

export default App;
